CREATE TABLE `asignaturas` (
  `Num_Asig` int(100) NOT NULL,
  `Año` int(100) NOT NULL,
  `Semestre` varchar(100) NOT NULL,
  `Asignatura` varchar(100) NOT NULL,
  `Horas_de_clase` int(100) NOT NULL,
  `Horas_de_Lab` int(100) NOT NULL,
